parameters.jsCode:
// Group licenses by urgency
const licenses = $input.all();
const groups = {
  urgent: [],
  warning: [],
  notice: []
};

const now = new Date();

for (const license of licenses) {
  const expiryDate = new Date(license.json.expiry_date);
  const daysToExpiry = Math.ceil((expiryDate - now) / (1000 * 60 * 60 * 24));

  if (daysToExpiry <= 7) {
    groups.urgent.push({...license.json, days_to_expiry: daysToExpiry});
  } else if (daysToExpiry <= 14) {
    groups.warning.push({...license.json, days_to_expiry: daysToExpiry});
  } else {
    groups.notice.push({...license.json, days_to_expiry: daysToExpiry});
  }
}

return [{ json: groups }];

